@import url("https://fonts.googleapis.com/css2?family=Poppins:wght@100;200;300;400;500;600;700;800;900&display=swap");
*,
*::before,
*::after {
  box-sizing: border-box;
}
* {
  margin: 0;
}

html,
body {
  height: 100%;
  font-family: "Poppins", sans-serif;
}
body {
  line-height: 1.5;
  -webkit-font-smoothing: antialiased;
  -webkit-tap-highlight-color: rgba(0, 0, 0, 0);
}

img,
picture,
video,
canvas,
svg {
  display: block;
  max-width: 100%;
}
input,
button,
textarea,
select {
  font: inherit;
  outline: none;
  border: none;
}
p,
h1,
h2,
h3,
h4,
h5,
h6 {
  overflow-wrap: break-word;
}

body {
  position: relative;
}

.getGradient {
  padding: 15px;
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 30px;
}

span {
  display: block;
  height: 200px;
  border-radius: 12px;
  overflow: hidden;
  cursor: pointer;
  padding: 40px;
  font-size: 18px;
  font-weight: 600;
}

@keyframes onClick {
  0% {
    opacity: 0;
    visibility: hidden;
  }
  50% {
    opacity: 1;
    visibility: visible;
  }
  100% {
    opacity: 0;
    visibility: hidden;
  }
}

.overlay {
  position: absolute;
  inset: 0;
  animation-name: onClick;
  animation-duration: 1.25s;
  display: flex;
  align-items: center;
  z-index: 100;
}

.overlay span {
  background: white;
  padding: 86px 0;
  width: 100%;
  border-radius: 0;
  font-size: 48px;
  font-weight: 700;
  display: flex;
  justify-content: center;
  align-items: center;
}

body {
  width: 100%;
}
